const input = typeof query === 'string' ? query : JSON.stringify(query);

function section(title, bullets) {
  return `## ${title}\n${bullets.map(b => `- ${b}`).join('\n')}`;
}

const slides = [
  {
    t: '1) Title / Campaign Snapshot',
    b: [
      'Campaign name + one-line promise',
      'Brand + product/offer',
      'Dates + primary locations/placements',
      'Primary KPI (awareness, footfall, promo redemptions, etc.)',
    ],
  },
  {
    t: '2) Objective & Audience',
    b: [
      'Objective (what changes in the real world?)',
      'Primary audience + context (commute, shopping, events)',
      'Key insight / tension',
      'What we want them to do next',
    ],
  },
  {
    t: '3) Message Strategy (3-Second Rule)',
    b: [
      'Single core message (<= 7 words)',
      'Supporting line (optional)',
      'Reason-to-believe (short)',
      'CTA (QR / URL / store / app)',
    ],
  },
  {
    t: '4) Creative System (Board Specs)',
    b: [
      'Logo placement + safe area',
      'Hierarchy: Headline > visual > CTA',
      'Contrast & legibility guidance',
      'Localization rules (city/offer/date)',
    ],
  },
  {
    t: '5) Board Concepts (A/B/C)',
    b: [
      'Concept A: headline + visual direction + CTA',
      'Concept B: headline + visual direction + CTA',
      'Concept C: headline + visual direction + CTA',
      'Notes on when to use each concept',
    ],
  },
  {
    t: '6) Placement & Flighting',
    b: [
      'Placement types (billboard, transit, retail, digital)',
      'Dayparting / rotation plan (if DOOH)',
      'Geos + rationale (high intent / high traffic)',
      'Contingencies (weather, supply, approvals)',
    ],
  },
  {
    t: '7) Measurement Plan',
    b: [
      'What we’ll measure (reach, frequency, lift)',
      'Methods (QR scans, store lift, geo-exposed tests)',
      'Reporting cadence',
      'Success thresholds',
    ],
  },
  {
    t: '8) Assets Checklist & Next Steps',
    b: [
      'Files: layered source, exports per format, legal lines',
      'Approvals: brand, legal, media owner specs',
      'Production timeline',
      'Next steps: finalize copy, design, trafficking, QA',
    ],
  },
];

let out = `You asked for an ad-board presentation.\n\nBrief received:\n${input}\n\n`;

for (const s of slides) {
  out += section(s.t, s.b) + '\n\n';
  out += `Speaker notes: Keep each point punchy and optimized for OOH viewing distance. Confirm sizes/specs with media owner.\n\n`;
}

return out;

const input = typeof query === 'string' ? query : JSON.stringify(query);

function section(title, bullets) {
  return `## ${title}\n${bullets.map(b => `- ${b}`).join('\n')}`;
}

const slides = [
  {
    t: '1) Title / Executive Summary',
    b: [
      'What we’re presenting (campaign/plan)',
      'Who it’s for + timeframe',
      'The single most important outcome',
    ],
  },
  {
    t: '2) Context & Problem',
    b: [
      'Market / customer context (1-2 facts)',
      'Primary challenge or opportunity',
      'Why now (timing, trend, trigger)',
    ],
  },
  {
    t: '3) Audience & Insight',
    b: [
      'Primary segments + jobs-to-be-done',
      'Key insight that changes strategy',
      'Barriers to conversion + motivators',
    ],
  },
  {
    t: '4) Positioning & Messaging',
    b: [
      'Value proposition (one sentence)',
      'Message pillars (3)',
      'Proof points / RTBs',
      'CTA & offer (if applicable)',
    ],
  },
  {
    t: '5) Strategy & Plan (High Level)',
    b: [
      'Approach (acquire/activate/retain)',
      'Campaign theme + creative idea',
      'Funnel mapping (awareness → conversion)',
      'Assumptions & risks',
    ],
  },
  {
    t: '6) Channel Mix & Tactics',
    b: [
      'Paid: search/social/programmatic (where/why)',
      'Owned: email/site/product (how we leverage)',
      'Earned/partners: PR, creators, affiliates',
      'Experiments: 2-3 test ideas + hypotheses',
    ],
  },
  {
    t: '7) Timeline & Milestones',
    b: [
      'Phases (prep, launch, optimize, wrap)',
      'Key deliverables per phase',
      'Dependencies (creative, tracking, approvals)',
      'Weekly cadence',
    ],
  },
  {
    t: '8) Measurement (KPIs) & Reporting',
    b: [
      'North-star metric + supporting KPIs',
      'Attribution approach (what we can/can’t claim)',
      'Dashboards + reporting schedule',
      'Definition of success',
    ],
  },
  {
    t: '9) Budget & Resourcing (Optional)',
    b: [
      'Budget allocation by channel',
      'Expected ranges for CAC/CPA (if known)',
      'Team roles and owners',
      'External vendors/tools',
    ],
  },
  {
    t: '10) Next Steps',
    b: [
      'Decisions needed (2-4)',
      'Immediate actions (this week)',
      'Risks to mitigate',
      'Kickoff date',
    ],
  },
];

let out = `You asked for a marketing presentation.\n\nBrief received:\n${input}\n\n`;

for (const s of slides) {
  out += section(s.t, s.b) + '\n\n';
  out += `Speaker notes: Tie each slide back to goals and audience. Keep it decision-oriented and include measurable outcomes.\n\n`;
}

out += section('Assets checklist', [
  'Brand guidelines + logo pack',
  'Product screenshots / UI flows',
  'Proof points (stats, testimonials, case studies)',
  'Tracking plan (UTMs/events) and dashboard access',
]) + '\n';

return out;
